import React, { useState } from 'react';
import PropTypes from 'prop-types';

ColorBox.propTypes = {
    
};
function getRandomColor(){
    const COLOR_LIST = ['red','blue','black','green','yellow','deeppink'];
    const random = Math.trunc(Math.random()*6);
    return COLOR_LIST[random];
}

function ColorBox() {
    const [color,setColor] = useState(localStorage.getItem('local_color') || 'deeppink');
    function changeColor(){
        const newColor = getRandomColor();
        localStorage.setItem('local_color',newColor);
        return setColor(newColor);
    }
    return (
        <div 
        className="color-box"
        style={{backgroundColor:color}}
        onClick={changeColor}
        >   
        </div>
    );
}

export default ColorBox;
