        <Person changed={switchChangeInput} click={changeClick} name={person[2].name} age={person[2].age}/> */}
        <Click />
        <button style={stylecustom} onClick={()=>changeToggle()}>Toggle</button>
        {personList}
      </div>
      </>
    );
  }


export default App;
